# Enable logging
logging.basicConfig(format="%(asctime)s - %(name)s - %(levelname)s - %(message)s", level=logging.INFO)
logger = logging.getLogger(__name__)

# Define states for the conversation using a dictionary
QUESTIONS = {
    "GF_NAME": ("Let's begin! What is your partner's name?", "USER_NAME"),
    "USER_NAME": ("Lovely! Now, what is your name or nickname?", "INTRO"),
    "INTRO": ("Perfect. What short introductory message would you like to write?", "SONG"),
    "SONG": ("Great. Now, please provide a direct URL to a song (`.mp3` format).\n\n*Tip: Upload your song to [archive.org](https://archive.org) to get a direct link.*", "MEMORY1_PHOTO"),
    "MEMORY1_PHOTO": ("Now for your memories! First, send a direct photo URL for your first memory.\n\n*Tip: Upload photos to [postimages.org](https://postimages.org) and use the 'Direct link'.*", "MEMORY1_TITLE"),
    "MEMORY1_TITLE": ("What is the title for this memory (e.g., 'The Day We Met')?", "MEMORY1_DESC"),
    "MEMORY1_DESC": ("And a short description for this memory?", "MEMORY2_PHOTO"),
    "MEMORY2_PHOTO": ("Great! Now, a photo URL for your second memory.", "MEMORY2_TITLE"),
    "MEMORY2_TITLE": ("What's the title for this second memory?", "MEMORY2_DESC"),
    "MEMORY2_DESC": ("And the description for it?", "GALLERY_1"),
    "GALLERY_1": ("Time for the photo gallery! Send the link for Gallery Photo 1.", "GALLERY_2"),
    "GALLERY_2": ("Photo 2?", "GALLERY_3"),
    "GALLERY_3": ("Photo 3?", "GALLERY_4"),
    "GALLERY_4": ("Photo 4?", "GALLERY_5"),
    "GALLERY_5": ("Photo 5?", "GALLERY_6"),
    "GALLERY_6": ("And the last one, Photo 6?", "COUNTDOWN_OCCASION"),
    "COUNTDOWN_OCCASION": ("We're almost done! What is the special occasion for the countdown? (e.g., 'Our Anniversary', 'Your Birthday')", "COUNTDOWN_DATE"),
    "COUNTDOWN_DATE": ("What is the date for this occasion? (Format: YYYY-MM-DD)", "FINAL_HEADING"),
    "FINAL_HEADING": ("What should the final big heading say? (e.g., 'You are my everything')", "FINAL_MESSAGE"),
    "FINAL_MESSAGE": ("And the final closing message?", None) # None indicates the last question
}
# A single state for handling all replies
TYPING_REPLY = 1

async def start(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    user = update.effective_user
    logger.info(f"User {user.first_name} started the bot.")
    welcome_message = f"Hi {user.mention_html()}! üíñ\n\nWelcome to the **Romantic Page Generator Bot**!\nReady to begin? Type /create"
    await update.message.reply_html(welcome_message)
    return ConversationHandler.END

async def create(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    context.user_data.clear()
    context.user_data['answers'] = {}
    
    # Ask the first question
    first_question_key = "GF_NAME"
    question_text, _ = QUESTIONS[first_question_key]
    context.user_data['current_question_key'] = first_question_key
    
    await update.message.reply_text(question_text, parse_mode='Markdown')
    return TYPING_REPLY

async def handle_reply(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    current_key = context.user_data.get('current_question_key')
    if not current_key:
        return ConversationHandler.END

    # Store the answer
    context.user_data['answers'][current_key] = update.message.text
    
    # Determine the next question
    _text, next_key = QUESTIONS[current_key]

    if next_key:
        # Ask the next question
        next_question_text, _ = QUESTIONS[next_key]
        context.user_data['current_question_key'] = next_key
        await update.message.reply_text(next_question_text, parse_mode='Markdown')
        return TYPING_REPLY
    else:
        # All questions are done, generate the file
        await update.message.reply_text("Perfect! All data collected. Generating your custom HTML file now...")
        
        answers = context.user_data['answers']
        format_data = {
            'girlfriend_name': answers.get("GF_NAME"), 'user_name': answers.get("USER_NAME"),
            'intro_message': answers.get("INTRO"), 'song_url': answers.get("SONG"),
            'memory1_photo': answers.get("MEMORY1_PHOTO"), 'memory1_title': answers.get("MEMORY1_TITLE"),
            'memory1_desc': answers.get("MEMORY1_DESC"), 'memory2_photo': answers.get("MEMORY2_PHOTO"),
            'memory2_title': answers.get("MEMORY2_TITLE"), 'memory2_desc': answers.get("MEMORY2_DESC"),
            'gallery_photo1': answers.get("GALLERY_1"), 'gallery_photo2': answers.get("GALLERY_2"),
            'gallery_photo3': answers.get("GALLERY_3"), 'gallery_photo4': answers.get("GALLERY_4"),
            'gallery_photo5': answers.get("GALLERY_5"), 'gallery_photo6': answers.get("GALLERY_6"),
            'countdown_occasion': answers.get("COUNTDOWN_OCCASION"), 'countdown_date': answers.get("COUNTDOWN_DATE"),
            'final_heading': answers.get("FINAL_HEADING"), 'final_message': answers.get("FINAL_MESSAGE")
        }
        
        final_html = HTML_TEMPLATE.format(**format_data)
        html_file = BytesIO(final_html.encode('utf-8'))
        html_file.name = 'love_letter.html'

        await update.message.reply_document(
            document=html_file,
            caption="‚ú® **Your beautiful webpage code is ready!** ‚ú®\n\n"
                    "**Instructions:**\n"
                    "1. Open this file on a computer.\n"
                    "2. Copy ALL the text inside.\n"
                    "3. Go to Blogspot, create a new post, switch to **HTML View**, and paste it in.\n\n"
                    "Type /create to make another one.",
            parse_mode='Markdown'
        )
        context.user_data.clear()
        return ConversationHandler.END

async def cancel(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    await update.message.reply_text("Creation cancelled. Type /create to start over.")
    context.user_data.clear()
    return ConversationHandler.END

async def main_bot_logic():
    """Sets up and runs the bot application."""
    token = os.environ.get("TELEGRAM_TOKEN")
    if not token:
        logger.error("TELEGRAM_TOKEN environment variable not set.")
        return

    application = Application.builder().token(token).build()

    conv_handler = ConversationHandler(
        entry_points=[CommandHandler("create", create)],
        states={
            TYPING_REPLY: [MessageHandler(filters.TEXT & ~filters.COMMAND, handle_reply)],
        },
        fallbacks=[CommandHandler("cancel", cancel)],
    )

    application.add_handler(CommandHandler("start", start))
    application.add_handler(conv_handler)
    
    logger.info("Starting bot polling...")
    await application.run_polling()


if __name__ == "__main__":
    # Run the Flask server in a separate thread
    flask_thread = threading.Thread(target=run_flask)
    flask_thread.daemon = True
    flask_thread.start()
    
    # Run the bot's asyncio event loop
    asyncio.run(main_bot_logic())