# --- Flask Web Server Setup (This part is correct and remains) ---
app = Flask(__name__)
@app.route('/')
def hello():
    return "Bot is alive!"
def run_flask():
    port = int(os.environ.get('PORT', 8080))
    app.run(host='0.0.0.0', port=port)

