# Enable logging and define states
logging.basicConfig(format="%(asctime)s - %(name)s - %(levelname)s - %(message)s", level=logging.INFO)
logger = logging.getLogger(__name__)

# Define states for the conversation using a dictionary for clarity
CHOOSING, TYPING_REPLY = range(2)
# Define keys for user_data
(
    GF_NAME, USER_NAME, INTRO, SONG, MEMORY1_PHOTO, MEMORY1_TITLE, MEMORY1_DESC,
    MEMORY2_PHOTO, MEMORY2_TITLE, MEMORY2_DESC, GALLERY_1, GALLERY_2,
    GALLERY_3, GALLERY_4, GALLERY_5, GALLERY_6, COUNTDOWN_OCCASION,
    COUNTDOWN_DATE, FINAL_HEADING, FINAL_MESSAGE
) = (
    "girlfriend_name", "user_name", "intro_message", "song_url", "memory1_photo",
    "memory1_title", "memory1_desc", "memory2_photo", "memory2_title", "memory2_desc",
    "gallery_photo1", "gallery_photo2", "gallery_photo3", "gallery_photo4",
    "gallery_photo5", "gallery_photo6", "countdown_occasion", "countdown_date",
    "final_heading", "final_message"
)

# --- Conversation Functions ---
async def start(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    user = update.effective_user
    logger.info(f"User {user.first_name} started the bot.")
    welcome_message = (
        f"Hi {user.mention_html()}! 💖\n\n"
        "Welcome to the **Romantic Page Generator Bot**!\n\n"
        "I'll help you create a beautiful, animated webpage for your special someone.\n\n"
        "Ready to begin? Type /create"
    )
    await update.message.reply_html(welcome_message, disable_web_page_preview=True)
    return ConversationHandler.END

async def create(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    context.user_data.clear()
    context.user_data['next_question'] = GF_NAME
    await update.message.reply_text("Let's begin! What is your partner's name?")
    return TYPING_REPLY

async def received_information(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    user_data = context.user_data
    current_question = user_data.get('next_question')
    text = update.message.text
    
    # Store the answer
    user_data[current_question] = text

    # Logic to ask the next question
    if current_question == GF_NAME:
        user_data['next_question'] = USER_NAME
        await update.message.reply_text("Lovely! Now, what is your name or nickname?")
        return TYPING_REPLY
    elif current_question == USER_NAME:
        user_data['next_question'] = INTRO
        await update.message.reply_text("Perfect. What short introductory message would you like to write?")
        return TYPING_REPLY
    elif current_question == INTRO:
        user_data['next_question'] = SONG
        await update.message.reply_text("Great. Now, please provide a direct URL to a song (`.mp3` format).\n\n*Tip: Upload your song to [archive.org](https://archive.org) to get a direct link.*", parse_mode='Markdown')
        return TYPING_REPLY
    # ...This pattern continues for all questions...
    elif current_question == SONG:
        user_data['next_question'] = MEMORY1_PHOTO
        await update.message.reply_text("Now for your memories! First, send a direct photo URL for your first memory.\n\n*Tip: Upload photos to [postimages.org](https://postimages.org) and use the 'Direct link'.*", parse_mode='Markdown')
        return TYPING_REPLY
    elif current_question == MEMORY1_PHOTO:
        user_data['next_question'] = MEMORY1_TITLE
        await update.message.reply_text("What is the title for this memory (e.g., 'The Day We Met')?")
        return TYPING_REPLY
    elif current_question == MEMORY1_TITLE:
        user_data['next_question'] = MEMORY1_DESC
        await update.message.reply_text("And a short description for this memory?")
        return TYPING_REPLY
    elif current_question == MEMORY1_DESC:
        user_data['next_question'] = MEMORY2_PHOTO
        await update.message.reply_text("Great! Now, a photo URL for your second memory.")
        return TYPING_REPLY
    elif current_question == MEMORY2_PHOTO:
        user_data['next_question'] = MEMORY2_TITLE
        await update.message.reply_text("What's the title for this second memory?")
        return TYPING_REPLY
    elif current_question == MEMORY2_TITLE:
        user_data['next_question'] = MEMORY2_DESC
        await update.message.reply_text("And the description for it?")
        return TYPING_REPLY
    elif current_question == MEMORY2_DESC:
        user_data['next_question'] = GALLERY_1
        await update.message.reply_text("Time for the photo gallery! Send the link for Gallery Photo 1.")
        return TYPING_REPLY
    elif current_question == GALLERY_1:
        user_data['next_question'] = GALLERY_2
        await update.message.reply_text("Photo 2?")
        return TYPING_REPLY
    elif current_question == GALLERY_2:
        user_data['next_question'] = GALLERY_3
        await update.message.reply_text("Photo 3?")
        return TYPING_REPLY
    elif current_question == GALLERY_3:
        user_data['next_question'] = GALLERY_4
        await update.message.reply_text("Photo 4?")
        return TYPING_REPLY
    elif current_question == GALLERY_4:
        user_data['next_question'] = GALLERY_5
        await update.message.reply_text("Photo 5?")
        return TYPING_REPLY
    elif current_question == GALLERY_5:
        user_data['next_question'] = GALLERY_6
        await update.message.reply_text("And the last one, Photo 6?")
        return TYPING_REPLY
    elif current_question == GALLERY_6:
        user_data['next_question'] = COUNTDOWN_OCCASION
        await update.message.reply_text("We're almost done! What is the special occasion for the countdown? (e.g., 'Our Anniversary', 'Your Birthday')")
        return TYPING_REPLY
    elif current_question == COUNTDOWN_OCCASION:
        user_data['next_question'] = COUNTDOWN_DATE
        await update.message.reply_text("What is the date for this occasion? (Format: YYYY-MM-DD)")
        return TYPING_REPLY
    elif current_question == COUNTDOWN_DATE:
        user_data['next_question'] = FINAL_HEADING
        await update.message.reply_text("What should the final big heading say? (e.g., 'You are my everything')")
        return TYPING_REPLY
    elif current_question == FINAL_HEADING:
        user_data['next_question'] = FINAL_MESSAGE
        await update.message.reply_text("And the final closing message?")
        return TYPING_REPLY
    elif current_question == FINAL_MESSAGE:
        # Last question answered, generate the file
        await update.message.reply_text("Perfect! All data collected. Generating your custom HTML file now...")
        final_html = HTML_TEMPLATE.format(**user_data)
        html_file = BytesIO(final_html.encode('utf-8'))
        html_file.name = 'love_letter.html'
        await update.message.reply_document(
            document=html_file,
            caption="✨ **Your beautiful webpage code is ready!** ✨\n\n"
                    "**Instructions:**\n"
                    "1. Open this file on a computer.\n"
                    "2. Copy ALL the text inside.\n"
                    "3. Go to Blogspot, create a new post, switch to **HTML View**, and paste it in.\n\n"
                    "Type /create to make another one.",
            parse_mode='Markdown'
        )
        user_data.clear()
        return ConversationHandler.END

async def cancel(update: Update, context: ContextTypes.DEFAULT_TYPE) -> int:
    await update.message.reply_text("Creation cancelled. Type /create to start over.")
    context.user_data.clear()
    return ConversationHandler.END

def run_bot() -> None:
    token = os.environ.get("TELEGRAM_TOKEN")
    if not token:
        logger.error("Error: TELEGRAM_TOKEN environment variable not set.")
        return

    application = Application.builder().token(token).build()

    conv_handler = ConversationHandler(
        entry_points=[CommandHandler("create", create)],
        states={
            TYPING_REPLY: [MessageHandler(filters.TEXT & ~filters.COMMAND, received_information)],
        },
        fallbacks=[CommandHandler("cancel", cancel)],
    )

    application.add_handler(CommandHandler("start", start))
    application.add_handler(conv_handler)
    
    threading.Thread(target=application.run_polling).start()
    logger.info("Telegram bot has started polling.")

if __name__ == "__main__":
    run_bot()
    run_flask()